changeSet.sql:
CREATE TABLE auditlog.notification ( id BIGINT(20) NOT NULL AUTO_INCREMENT, timestamp DATETIME NOT NULL, entity_name VARCHAR(255) NOT NULL, entity_id BIGINT(20) NOT NULL, event VARCHAR(45) NOT NULL, delivered DATETIME NULL DEFAULT NULL, source_entities VARCHAR(8192), source VARCHAR(1024), notification VARCHAR(1024) NOT NULL,
PRIMARY KEY (id),
INDEX idx_notification_timestamp(timestamp), INDEX idx_notification_delivered(delivered), INDEX idx_notification_entity_name_id(entity_name, entity_id) );
