changeSet.sql:
CREATE TABLE if not exists prodlims.insurance_company ( id BIGINT(20) NOT NULL AUTO_INCREMENT, payor_id VARCHAR(255) NULL, name VARCHAR(255) NULL, address_line_1 VARCHAR(255) NULL, address_line_2 VARCHAR(255) NULL, city VARCHAR(255) NULL, state VARCHAR(2) NULL, country_code VARCHAR(2) NULL,
PRIMARY KEY (id),
INDEX idx_insurance_company__payor_id (payor_id) );
