CREATE TABLE `prodlims`.`casesharingconsent` ( `id` bigint(20) NOT NULL AUTO_INCREMENT, `firstname` varchar(255) DEFAULT NULL, `middlename` varchar(255) DEFAULT NULL, `lastname` varchar(255) DEFAULT NULL, `dateofbirth` datetime DEFAULT NULL, `hassinglename` bit(1) DEFAULT b'0', `casefile_id` bigint(20) NOT NULL, `casesharing_id` bigint(20) DEFAULT NULL, `casesharingtype` varchar(255) NOT NULL,
PRIMARY KEY (`id`), UNIQUE KEY (`casesharing_id`), KEY `FKcasesharingconsent_casefile` (`casefile_id`), KEY `FKcasesharingconsent_casesharing` (`casesharing_id`),
CONSTRAINT `FKcasesharingconsent_casefile` FOREIGN KEY (`casefile_id`) REFERENCES `casefile` (`id`), CONSTRAINT `FKcasesharingconsent_casesharing` FOREIGN KEY (`casesharing_id`) REFERENCES `casesharing` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;
