changeSet.sql:
SET SQL_SAFE_UPDATES=0;
UPDATE horizondiseasetemplate
SET VARIANT = REPLACE(VARIANT,"COL4A3","COL4A4")
WHERE diseasename="Alport Syndrome, COL4A4-Related"
AND gene="COL4A4"
AND templatecode IN ("pathogenic variant detected","likely pathogenic variant detected","2 variants detected (cis or trans unknown)","2 variants detected (cis)","2 variants detected (trans)");
SET SQL_SAFE_UPDATES=1;
