changeSet.sql:
SET SQL_SAFE_UPDATES=0;
UPDATE horizon_risk_data
SET riskbefore="unknown"
WHERE riskbefore="n/a";

UPDATE horizon_risk_data
SET riskafter="unknown"
WHERE riskafter="n/a";
SET SQL_SAFE_UPDATES=1;

SET SQL_SAFE_UPDATES=0;

UPDATE horizon_risk_data
SET ethnicity="African American"
WHERE ethnicity="African";

SET SQL_SAFE_UPDATES=1;
