changeSet.sql:
INSERT INTO `prodlims`.`categorygroup` (name, fordisplay)
VALUES ('Support Review Exempt', 0);

INSERT INTO `prodlims`.`categorygroup_category` (categorygroup_id, category_id)
SELECT (SELECT id FROM `prodlims`.`categorygroup` WHERE name = 'Support Review Exempt') AS categorygroup_id, id
FROM `prodlims`.`category`
WHERE name IN ('Legal Hold', 'Lab Hold', 'Report Hold');

changeSet.rollback:
DELETE
FROM `prodlims`.`categorygroup_category`
WHERE categorygroup_id = (SELECT id FROM `prodlims`.`categorygroup` WHERE name = 'Support Review Exempt');

DELETE
FROM `prodlims`.`categorygroup`
WHERE name = 'Support Review Exempt';
