changeSet.sql:
alter table prodlims.gctemplate add title varchar(100) null;
update prodlims.gctemplate set title = "Test Result Delay Notification" where delayednote and id != 0;
update prodlims.gctemplate set title = "Genetic Information Session Summary" where not delayednote and id != 0;

alter table prodlims.gctemplate add type varchar(50) not null;
update prodlims.gctemplate set type = "COMMON" where id != 0;
update prodlims.gctemplate set type = "HORIZON" where name like "%Horizon%" and not archived and id != 0;
